use rust_xlsxwriter::{Workbook, XlsxError};

mod common;

// Test case to demonstrate creating a basic file with 3 worksheets and no data.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new(filename);
    _ = workbook.add_worksheet();
    _ = workbook.add_worksheet();
    _ = workbook.add_worksheet();

    workbook.close()?;

    Ok(())
}

#[test]
fn bootstrap02_multiple_worksheets() {
    let test_runner = common::TestRunner::new("bootstrap02").initialize();

    _ = create_new_xlsx_file(test_runner.output_file());

    test_runner.assert_eq();
    test_runner.cleanup();
}
