use rust_xlsxwriter::{Format, Workbook, XlsxError};

mod common;

// Test case to demonstrate creating a basic file with number formatting.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let format1 = Format::new().set_bold();
    let format2 = Format::new().set_italic();
    let format3 = Format::new().set_num_format("0.0");

    let worksheet = workbook.add_worksheet();
    worksheet.write_number(0, 0, 1, &format1)?;
    worksheet.write_number(1, 1, 2, &format2)?;
    worksheet.write_number(2, 2, 3, &format3)?;

    workbook.save(filename)?;

    Ok(())
}

#[test]
fn bootstrap13_number_format() {
    let test_runner = common::TestRunner::new("bootstrap13").initialize();

    _ = create_new_xlsx_file(test_runner.output_file());

    test_runner.assert_eq();
    test_runner.cleanup();
}
