use rust_xlsxwriter::{Format, Workbook, XlsxError};

mod common;

// Test case to demonstrate creating a basic file with boolean types.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new(filename);
    let worksheet = workbook.add_worksheet();

    let format1 = Format::new().set_bold();

    worksheet.write_boolean_only(0, 0, true)?;
    worksheet.write_boolean_only(1, 0, false)?;
    worksheet.write_boolean(2, 0, true, &format1)?;
    worksheet.write_boolean(3, 0, false, &format1)?;

    workbook.close()?;

    Ok(())
}

#[test]
fn bootstrap37_boolean_types() {
    let testcase = "bootstrap37";

    let (excel_file, xlsxwriter_file) = common::get_xlsx_filenames(testcase);
    _ = create_new_xlsx_file(&xlsxwriter_file);
    common::assert_eq(&excel_file, &xlsxwriter_file);
    common::remove_test_xlsx_file(&xlsxwriter_file);
}
