use rust_xlsxwriter::{Workbook, XlsxError};

mod common;

// Test case to demonstrate setting various page setup methods.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new(filename);

    let worksheet = workbook.add_worksheet();
    worksheet.write_string_only(0, 0, "Foo")?;

    worksheet.set_repeat_rows(0, 0)?;

    workbook.close()?;

    Ok(())
}

#[test]
fn bootstrap44_page_setup() {
    let test_runner = common::TestRunner::new("bootstrap44").initialize();

    _ = create_new_xlsx_file(test_runner.output_file());

    test_runner.assert_eq();
    test_runner.cleanup();
}
