use rust_xlsxwriter::{FilterCondition, FilterCriteria, Workbook, XlsxError};

mod common;

// Test to demonstrate autofilters.
fn create_new_xlsx_file(filename: &str) -> Result<(), XlsxError> {
    let mut workbook = Workbook::new();

    let worksheet = workbook.add_worksheet();

    // Write the headers.
    worksheet.write_string_only(0, 0, "Region").unwrap();
    worksheet.write_string_only(0, 1, "Item").unwrap();
    worksheet.write_string_only(0, 2, "Volume").unwrap();
    worksheet.write_string_only(0, 3, "Month").unwrap();

    // Write the data used in the autofilter.
    let data = common::get_autofilter_data();
    for (row, data) in data.iter().enumerate() {
        let row = 1 + row as u32;
        worksheet.write_string_only(row, 0, data.0)?;
        worksheet.write_string_only(row, 1, data.1)?;
        worksheet.write_number_only(row, 2, data.2)?;
        worksheet.write_string_only(row, 3, data.3)?;
    }

    worksheet.autofilter(0, 0, 50, 3)?;

    let filter_condition1 = FilterCondition::new()
        .custom1_string(FilterCriteria::NotEqualTo, "South")
        .custom2_string(FilterCriteria::EqualTo, "North");

    worksheet.filter_column(0, &filter_condition1)?;

    let filter_condition2 = FilterCondition::new()
        .custom1_string(FilterCriteria::GreaterThan, "Apple")
        .custom2_string(FilterCriteria::LessThan, "Pear")
        .custom_and_condition();

    worksheet.filter_column(1, &filter_condition2)?;

    let filter_condition3 = FilterCondition::new()
        .custom1_number(FilterCriteria::GreaterThanOrEqualTo, 3000)
        .custom2_number(FilterCriteria::LessThanOrEqualTo, 9000)
        .custom_and_condition();

    worksheet.filter_column(2, &filter_condition3)?;

    let filter_condition4 = FilterCondition::new()
        .custom1_string(FilterCriteria::GreaterThanOrEqualTo, "February")
        .custom2_string(FilterCriteria::LessThanOrEqualTo, "November")
        .custom_and_condition();

    worksheet.filter_column(3, &filter_condition4)?;

    workbook.save(filename)?;

    Ok(())
}
#[test]
fn test_bootstrap57() {
    let test_runner = common::TestRunner::new()
        .set_name("bootstrap57")
        .set_function(create_new_xlsx_file)
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
